'use strict';

// given a dialect name, return the class
var getDialect = function(dialect) {
  switch (dialect.toLowerCase()) {
    case 'postgres':
      return require('sql/lib/dialect/postgres');
    case 'mysql':
      return require('sql/lib/dialect/mysql');
    case 'sqlite':
      return require('sql/lib/dialect/sqlite');
    case 'mssql':
      return require('sql/lib/dialect/mssql');
    case 'oracle':
      return require('sql/lib/dialect/oracle');
    default:
      throw new Error(dialect + ' is unsupported');
  }
};

module.exports = getDialect;
